async function main (ev) {
    enableInputs(false);
    buildEmojiMap();
    const imgSrc = image.files[0] ? URL.createObjectURL(image.files[0]) : myURL;
    const img = await loadImage(imgSrc);
    analyzeImage(img);
}

async function loadImage (src) {
    return new Promise((resolve, reject) => {
        const img = new Image();
        img.crossOrigin = 'Anonymous';
        img.onload = () => resolve(img);
        img.onerror = reject;
        img.src = src;
    });
}

async function analyzeImage (img) {
    progressBar.style.display = 'inline';
    await delay(100);
    const ROWS = Math.floor(img.naturalHeight / pSize.value);
    const COLS = Math.floor(img.naturalWidth / pSize.value);
    result.width = COLS * scale.value;
    result.height = (ROWS * scale.value) - scale.value;

    const x = result.getContext('2d');
    x.clearRect(0, 0, result.width, result.height);
    if (enableBgColor.checked) {
        x.fillStyle = bgColor.value;
        x.fillRect(0, 0, result.width, result.height);
    }
    x.font = scale.value * (display.value == 'chart' ? 0.75 : display.value == 'mosaic' ? 1.5 : 1) + 'px Segoe UI Emoji, Apple Color Emoji, Segoe UI Symbol, Noto Color Emoji';

    let hueMap = {};
    const pixels = [];
    for (let i = 0; i < ROWS * COLS; i += 1) {
        let row = Math.floor(i / COLS);
        let col = i % COLS;
        let buffer = hiddenX.getImageData(col * pSize.value, row * pSize.value, pSize.value, pSize.value);
        let pixel = getPixel(buffer.data);
        pixels.push(pixel);
        hueMap[pixel.hue] = (hueMap[pixel.hue] || 0) + 1;
    }

    const sortedHues = Object.entries(hueMap)
        .sort((a, b) => b[1] - a[1])
        .map(entry => parseInt(entry[0]));

    let hueIndex = 0;
    for (let i = 0; i < ROWS * COLS; i += 1) {
        let row = Math.floor(i / COLS);
        let col = i % COLS;
        let pixel = pixels[i];
        let xPos = col * scale.value;
        let yPos = row * scale.value;
        let char = getEmojiChar(pixel.hue, sortedHues, hueIndex);
        let color = getEmojiColor(pixel, char, i);
        x.fillStyle = color;
        x.fillText(char, xPos, yPos + scale.value);
        hueIndex = (hueIndex + 1) % sortedHues.length;
    }

    progressBar.style.display = 'none';
    enableInputs(true);
}

function enableInputs (enabled) {
    const inputs = [image, pSize, scale, tolerance, fillColor, display, hideBlack, emptySpace, enableBgColor, bgColor];
    inputs.forEach(input => (input.disabled = !enabled));
}

function delay (ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

function getPixel (data) {
    let r = data[0];
    let g = data[1];
    let b = data[2];
    let a = data[3] / 255;
    let [h, s, l] = rgbToHsl(r, g, b);
    return { r, g, b, a, h, s, l };
}

function rgbToHsl (r, g, b) {
    r /= 255;
    g /= 255;
    b /= 255;
    let max = Math.max(r, g, b);
    let min = Math.min(r, g, b);
    let h, s, l = (max + min) / 2;

    if (max === min) {
        h = s = 0; // achromatic
    } else {
        let d = max - min;
        s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
        switch (max) {
            case r:
                h = (g - b) / d + (g < b ? 6 : 0);
                break;
            case g:
                h = (b - r) / d + 2;
                break;
            case b:
                h = (r - g) / d + 4;
                break;
        }
        h /= 6;
    }

    return [h * 360, s * 100, l * 100];
}

function getEmojiChar (hue, sortedHues, hueIndex) {
    let totalHues = sortedHues.length;
    let hueRatio = hueIndex / totalHues;
    let charIndex = Math.floor(hueRatio * moji.textContent.length);
    return moji.textContent[charIndex];
}

function getEmojiColor (pixel, char, index) {
    let r = pixel.r;
    let g = pixel.g;
    let b = pixel.b;
    let a = pixel.a;

    if (display.value === "Cores Originais") {
        return `rgba(${r}, ${g}, ${b}, ${a})`;
    } else if (display.value === "Preto e Branco") {
        let luminance = (0.299 * r + 0.587 * g + 0.114 * b) / 255;
        return luminance > 0.5 ? "#FFFFFF" : "#000000";
    } else if (display.value === "Cor Única") {
        return fillColor.value;
    } else if (display.value === "Gradiente") {
        let totalPixels = ROWS * COLS;
        let gradientStart = index / totalPixels;
        let gradientEnd = (index + 1) / totalPixels;
        let gradient = x.createLinearGradient(0, 0, scale.value, 0);
        gradient.addColorStop(0, fillColor.value);
        gradient.addColorStop(1, bgColor.value);
        return gradient;
    } else {
        return "#000000";
    }
}


